import React, { Component } from 'react';

import './App.css';

import $ from "jquery";


class MusicBox extends Component {

    constructor(props) {
      super(props);
      this.state = {
        player: null,
      }
    }

    componentDidMount(){

    }


    // //     // this function gets called when API is ready to use
    // onYouTubeIframeAPIReady() {
    //     // create the global player from the specific iframe (#video)
    //     this.state.player = new YT.Player('video', {
    //     events: {
    //         // call this function when player is ready to use
    //         'onReady': onPlayerReady
    //     }
    //     });
    // }



    start() {
        $(document).ready(function() {
            $('#play').on('click', function(ev) {

              $("#video")[0].src += "&autoplay=1";
              ev.preventDefault();

            });
        });
    }

    render() {
        // youtubeLink = https://www.youtube.com/watch?v=nEqiOT_-Z_o
        let embedLink = this.props.youtubeLink.substring(0, 23) + "/embed/" + this.props.youtubeLink.substring(32) + "?rel=0";
        // youtubeLink = https://www.youtube.com/embed/nEqiOT_-Z_o?rel=0
        return(
            <div id="album-box">
                <button onClick={this.start} id="play"></button>
                <img className="album-art" src={this.props.albumImage}></img>
                <br></br>
                <div style={{position:"relative",width:"62px", height:"35px", overflow:"hidden", display:"inline-block"}}>
                    <div style={{position:"absolute",top:"-266px",left:"-5px"}}>    
                        <iframe width="300" height="300" src={embedLink} id="video"></iframe>
                    </div>
                </div>
            </div>
        );
    }

}
export default MusicBox;
