import React, { Component } from 'react';
// import logo from './logo.svg';
import './App.css';
import facebookIcon from './icons/facebook.png';
import githubIcon from './icons/github.png';
import linkedinIcon from './icons/linkedin.png';
import wordpressIcon from './icons/wordpress.png';

import MusicBox from "./MusicBox.js";

const MORE_MUSIC = 4;
const TYPE_SPEED = 150;

// import lorde from "pure-heroine.jpg";
// import square2 from ".square-two.png";
// import saturation3 from ".saturation-iii.jpg";
// import interstellar from ".interstellar.jpg";
// import nvdes from ".nvdes  .jpg";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      musicBox: [],
      phrase: "Welcome!"
    }
  }

  componentDidMount() {
    this.newSelection();
    this.typeAnimation(0);
  }


  typeAnimation(i) {
    let phrases = ["Howdy", "Aloha", "Hola", "Hello", "こんにちは", "Hallo", "Bonjour", "Ciao", "Goedendag", "안녕하세요", "你好", "สวัสดี", "Привет", "Καλημέρα", "नमस्ते", "مرحبا"];

    let p = document.getElementById("type-animation");
    let txt = this.state.phrase;
    if (i < txt.length) {
      p.innerText += "" + txt.charAt(i);
      i++;
      setTimeout(() => this.typeAnimation(i)
      , TYPE_SPEED);
    } else if (i <= txt.length * 2) {
      let subtractIndex = i - txt.length;
      p.innerText = txt.substring(0, txt.length - subtractIndex);
      i++;
      setTimeout(() => this.typeAnimation(i)
      , TYPE_SPEED);
    } else {
      let randomPhrase = Math.floor(Math.random() * phrases.length);
      this.setState({phrase: phrases[randomPhrase]});
      this.typeAnimation(0); 
    }
  }


  changeFont() {
    let fonts = ["https://fontmeme.com/permalink/190105/34666c4990fb8a12c872269f5a64f200.png", // doodle
                 "https://fontmeme.com/permalink/190105/b861b9e0f248b04187673a06cafdcf51.png", // comics
