import './App.css';

import $ from "jquery";


class MusicBox extends Component {

    constructor(props) {
      super(props);
      this.state = {
        player: null,
      }
    }

    componentDidMount(){

    }


    // //     // this function gets called when API is ready to use
    // onYouTubeIframeAPIReady() {
