    start() {

        // let playId = "#play" + this.props.index;
        // let id = "#video" + this.props.index;
        console.log(this.props);
        $(document).ready(function() {
            $(playId).on('click', function(ev) {

              $(id)[0].src += "&autoplay=1";
              ev.preventDefault();
            });
        });
    }

    render() {
        // youtubeLink = https://www.youtube.com/watch?v=nEqiOT_-Z_o
        let embedLink = this.props.youtubeLink.substring(0, 23) + "/embed/" + this.props.youtubeLink.substring(32) + "?rel=0";
        // youtubeLink = https://www.youtube.com/embed/nEqiOT_-Z_o?rel=0
        let playId = "play" + this.props.index;
        let id = "video" + this.props.index;
        return(
            <div id="album-box">
                <button onClick={this.start} id={playId}>START</button>
                <img className="album-art" src={this.props.albumImage}></img>
                <br></br>
                {/* <div style={{position:"relative",width:"62px", height:"50px", overflow:"hidden", display:"inline-block"}}>
                    <div style={{position:"absolute",top:"-266px",left:"-5px"}}>     */}
                        <iframe width="300" height="300" src={embedLink} id={id}></iframe>
                    {/* </div>
                </div> */}
            </div>
        );
    }

}
export default MusicBox;
