import React, { Component } from 'react';

import './App.css';

import $ from "jquery";


class MusicBox extends Component {

    constructor(props) {
      super(props);
      this.state = {
        player: null,
      }
    }



    start(buttonId, id) {
        buttonId = "#" + buttonId;
        id = "#" + id;
        // $(document).ready(function() {
        //     $(buttonId).on('click', function(ev) {
        //       $(id)[0].src += "&autoplay=1";
        //     });
        // });

        $(buttonId).click(function(){
            $(id)[0].contentWindow.postMessage('{"event":"command","func":"' + 'playVideo' + '","args":""}', '*');
        });
    }

    stop(buttonId, id) {
        buttonId = "#" + buttonId;
        id = "#" + id;
        $(buttonId).click(function(){
            $(id)[0].contentWindow.postMessage('{"event":"command","func":"' + 'stopVideo' + '","args":""}', '*');
        });
    }

    pause(buttonId, id) {
         buttonId = "#" + buttonId;
         id = "#" + id;


        $(buttonId).click(function(){
             $(id)[0].contentWindow.postMessage('{"event":"command","func":"' + 'pauseVideo' + '","args":""}', '*');
         });
    }

    render() {
        // youtubeLink = https://www.youtube.com/watch?v=nEqiOT_-Z_o
        let embedLink = this.props.youtubeLink.substring(0, 23) + "/embed/" + this.props.youtubeLink.substring(32) + "?enablejsapi=1&version=3&playerapiid=ytplayer";
        // youtubeLink = https://www.youtube.com/embed/nEqiOT_-Z_o

        let videoId = "video" + this.props.index;
        let id = this.props.index;

        $("#pause" + id).click(function(){
            console.log("HGELLO")
            $("#" + videoId)[0].contentWindow.postMessage('{"event":"command","func":"' + 'pauseVideo' + '","args":""}', '*');
        });



        return(
            <div id="album-box">
                <button onMouseEnter={() => this.start("start" + id, videoId)} id={"start" + id}>START</button>
                <button onClick={() => this.pause("pause" + id, videoId)} id={"pause" + id}>PAUSE</button>
                {/* <button id={"pause" + id}>PAUSE</button> */}

                <button onClick={() => this.stop("stop" + id, videoId)} id={"stop" + id}>STOP</button>
                <img className="album-art" src={this.props.albumImage}></img>
                <br></br>
                {/* <div style={{position:"relative",width:"62px", height:"50px", overflow:"hidden", display:"inline-block"}}>
                    <div style={{position:"absolute",top:"-266px",left:"-5px"}}>     */}
                        <iframe width="300" height="300" src={embedLink} id={videoId}></iframe>
                    {/* </div>
                </div> */}
            </div>
        );
    }

