    render() {
        // youtubeLink = https://www.youtube.com/watch?v=nEqiOT_-Z_o
        let embedLink = this.props.youtubeLink.substring(0, 23) + "/embed/" + this.props.youtubeLink.substring(32);

        console.log(embedLink);
        return(
            <div id="album-box">
                <button onClick={this.start}></button>
                <img className="album-art" src={this.props.albumImage}></img>
