import React, { Component } from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

// import logo from './logo.svg';
import './App.css';
import facebookIcon from './icons/facebook.png';
import githubIcon from './icons/github.png';
import linkedinIcon from './icons/linkedin.png';
import wordpressIcon from './icons/wordpress.png';

import MusicBox from "./MusicBox.js";

import Header from './Header.js';
import Footer from './Footer.js';

const MORE_MUSIC = 9;

const TYPE_SPEED = 200;

// import lorde from "pure-heroine.jpg";
// import square2 from ".square-two.png";
// import saturation3 from ".saturation-iii.jpg";
// import interstellar from ".interstellar.jpg";
// import nvdes from ".nvdes  .jpg";


class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      musicBox: [],
      phrase: "Welcome"//"Welcome!"
    }
  }

  componentDidMount() {
    this.typeAnimation(0);
  }

  calculateAlbumGrid() {
    let columnPercentage = 100 / Math.sqrt(MORE_MUSIC);
    let columnPercentageStatement = "";
    for (let i = 0; i < Math.floor(Math.sqrt(MORE_MUSIC)); i++ ) {
      columnPercentageStatement += columnPercentage + "% ";
    }
    let box = document.getElementById("music-div");
    box.setAttribute("style","grid-template-columns:" + columnPercentageStatement);
  }

  typeAnimation(i) {
    let phrases = ["Howdy", "Aloha", "Hola", "Hello", "こんにちは", "Hallo", "Bonjour", "שלום", "Ciao", "안녕하세요", "你好", "สวัสดี", "Привет", "Καλημέρα", "नमस्ते", "مرحبا"];
    let p = document.getElementById("type-animation");
    if (p) {
      let txt = this.state.phrase;
      if (i < txt.length) {
        p.innerText += "" + txt.charAt(i);
        i++;
        setTimeout(() => this.typeAnimation(i)
        , TYPE_SPEED);
      } else if (i <= txt.length * 2) {
        let subtractIndex = i - txt.length;
        p.innerText = txt.substring(0, txt.length - subtractIndex);
        i++;
        setTimeout(() => this.typeAnimation(i)
        , TYPE_SPEED);
      } else {
        let randomPhrase = Math.floor(Math.random() * phrases.length);
        this.setState({phrase: phrases[randomPhrase]});
        this.typeAnimation(0); 
      }
    }
  }

  changeFont() {
    let fonts = ["https://fontmeme.com/permalink/190105/34666c4990fb8a12c872269f5a64f200.png", // doodle
                 "https://fontmeme.com/permalink/190105/b861b9e0f248b04187673a06cafdcf51.png", // comics
                 "https://fontmeme.com/permalink/190105/d20c7c83635b87feaa88b3661e570380.png", // nouveau
                 "https://fontmeme.com/permalink/190105/eb4fe1a3f0fb6a10789edf6731d479d0.png", // typewriter
                 "https://fontmeme.com/permalink/190105/b0d4913c55ec53f29810bdd00be8f0b9.png", // magna carta
                 "https://fontmeme.com/permalink/190105/24b1f5ded5612f1c3ab1a446ba450ca5.png", // hearts
                 "https://fontmeme.com/permalink/190105/acf660a71f3f5e531a6fc33ec3b8d393.png", // blocky
                 "https://fontmeme.com/permalink/190105/90578442b3276021f5f32bbc0d61e3f5.png", // 8-bit
                 "https://fontmeme.com/permalink/190105/8baae864131bec9e65f235af4dc012b0.png", // calligraphy
                 "https://fontmeme.com/permalink/190105/a8590a3f8c7f634078e043f77f625695.png", // bauhaus
                 "https://fontmeme.com/permalink/190105/14e50937d1b6af4db8d0203170f2c9a4.png", // weebaneese
                 "https://fontmeme.com/permalink/190105/67560e9a9af0dd41928713e63e40f1dd.png", // ransom
                 "https://fontmeme.com/permalink/190105/997eb71762f01f9d1067f292609889d9.png", // broadway
                 "https://fontmeme.com/permalink/190105/c6c3829987542d73c3130b7397a78e4a.png", // metallica
                 "https://fontmeme.com/permalink/190105/a33cc24a746d69874a725179b6ac1d58.png", // minimal cursive
                 "https://fontmeme.com/permalink/190105/5fd50f6ade227ee71984cc390fca7d2d.png", // megaman
                ];
    let randomFont = Math.floor(Math.random() * fonts.length);
    let name = document.getElementById("name");

    name.setAttribute("src", fonts[randomFont]);
  }


  render() {

    return (

      <div>
        <Header></Header>
        <div className="coverImageBox">
           <div id="type-block">
              <h1 id="type-animation"></h1>
           </div>
        </div>
        <br></br>
        <div className="body">

          <h3>Hello!</h3>
          <p>My name is Trey Tamaki! I am a fourth-year student at the University of Washington, Seattle Campus.</p>
          <p>I am getting my B.S. in Computer Science with a minor in Art History with an intended graduation in Spring 2020.</p>
          <button onClick={() => this.newSelection()}>New Selection</button>
          <div id="music-div">
            {this.state.musicBox}
          </div>
        </div>
        <Footer></Footer>
      </div>
    ); 
  }
}

export default App;
