import React, { Component } from 'react';

import './css-files/launchpad.css';


import Header from './Header';
import Footer from './Footer';


class Launchpad extends Component {

  constructor(props) {
    super(props);
    this.state = {

    }
  }

  componentDidMount() {
  }

  render() {
    let buttons = [];
    for (let i = 0; i < 25; i++) {
        buttons.push(<LaunchpadButton id={"button" + i}></LaunchpadButton>);
    }

    return (

        <div>
            <Header></Header>
            <div className="cover-image-box" style={{ height:"131.32px" }}> </div>
            <br></br>
            <div className="body" >
                <div id="grid">
                    {buttons}
                </div>
            </div>
            <Footer></Footer>
        </div>
    ); 
  }
}
   // /music/glitch.mp3
class LaunchpadButton extends Component {
    constructor(props) {
      super(props);
      this.state = {

      }
    }

    changeColor(audio) {
        let duration = audio.duration;
        setTimeout(audio.setAttribute("style", "background-color: red;"), duration);

    }

    playAudio(time) {
        let audio = document.getElementById(this.props.id + "audio");
        audio.play();
        console.log(audio.duration);
        console.log("HEH");

        this.changeColor(audio);
        audio.setAttribute("style", "background-color: white;")
        setTimeout(
            () => this.playAudio(time)
        , time);
    }

    render() {
      return (

          <div className="box" onClick={() => this.playAudio(1000)}>
            <audio id={this.props.id + "audio"}><source src="/sound-files/glitch.mp3"></source></audio>
          </div>
      ); 
    }
  }

export default Launchpad;
